var FFI = require('ffi');
var ref = require('ref');
var Struct = require('ref-struct');

var VmbError_t = ref.types.int;
var lib = FFI.Library('../../../ThirdParty/AVT/VimbaC/Bin/Win64/VimbaC.dll',
  {
    'VmbStartup': [ VmbError_t, [ ] ],
    'VmbShutdown': [ 'void', [ ] ]
  });

module.exports = {};

function startup () {
  console.log('starting lib ...');
  lib.VmbStartup();
}
module.exports.startup = startup;

function shutdown () {
  console.log('shutdown');
  lib.VmbShutdown();
}
module.exports.shutdown = shutdown;
